import React, { useState, useEffect } from "react";

import { ContactForm } from "../../components/contactForm/ContactForm";
import { TileList } from "../../components/tileList/TileList";

export const ContactsPage = ({currentContacts, addContact}) => {
  /*
  Define state variables for 
  contact info and duplicate check
  */
 const [currentName, setCurrentName] = useState('');
 const [currentPhoneNumber, setCurrentPhoneNumber] = useState('');
 const [currentEmail, setCurrentEmail] = useState('');
 const [isDuplicate, setIsDuplicate] = useState(false);
 const [contacts, setContacts] = useState(currentContacts || []);

  const handleSubmit = (e) => {
    e.preventDefault();
    /*
    Add contact info and clear data
    if the contact name is not a duplicate
    */

   /*if(!isDuplicate){
    addContact({currentName, currentPhoneNumber, currentEmail});
    setCurrentName('');
    setCurrentPhoneNumber('');
    setCurrentEmail('');
    setIsDuplicate(false);
   }*/
   if (!isDuplicate && currentName && currentPhoneNumber && currentEmail) {
    const newContact = { currentName, currentPhoneNumber, currentEmail };
    setContacts((prevContacts) => [...prevContacts, newContact]);
    addContact(newContact); // Callback to notify the parent component
    // Clear the form after successful submission
    setCurrentName('');
    setCurrentPhoneNumber('');
    setCurrentEmail('');
    setIsDuplicate(false);
  }
  };

  /*
  Using hooks, check for contact name in the 
  contacts array variable in props
  */
  useEffect(() => {
    const isDuplicate = contacts.some((contact) => contact.currentName === currentName);
    setIsDuplicate(isDuplicate);
  }, [currentName, contacts]);
  

  return (
    <div>
      <section>
        <h2>Add Contact</h2> 
        <ContactForm
          name={currentName}
          phone={currentPhoneNumber}
          email={currentEmail}
          setName={setCurrentName}
          setPhone={setCurrentPhoneNumber}
          setEmail={setCurrentEmail}
          handleSubmit={handleSubmit}
          isNameDuplicate={setIsDuplicate}
        />
      </section>
      <hr />
      <section>
        <h2>Contacts</h2>
        <TileList data={contacts} />
      </section>
    </div>
  );
};
