import React, {useState} from "react";
import { RouterProvider, createBrowserRouter, createRoutesFromElements, Route, Navigate } from "react-router-dom"
import Root, { ROUTES } from "./components/root/Root";
import { AppointmentsPage } from "./containers/appointmentsPage/AppointmentsPage";
import { ContactsPage } from "./containers/contactsPage/ContactsPage";

function App() {
  /*
  Define state variables for 
  contacts and appointments 
  */
  const [contacts, setContacts] = useState([]);
  const [appointments, setAppointments] = useState([]);

  /*
  Implement functions to add data to
  contacts and appointments
  */
    const addContact = (name, phoneNumber, email) => {
    const newContact = {
      name: name, 
      phoneNumber: phoneNumber,
      email: email,
    };
    setContacts((prevContacts) => [...prevContacts, newContact]);
  };
    const addAppoint = (name, phoneNumber, email) => {
      const newAppoint={
        name: name, 
        phoneNumber: phoneNumber, 
        email: email,
      };
      setAppointments((prevAppointments) => [...prevAppointments, newAppoint]);
    };


  const router = createBrowserRouter(createRoutesFromElements(
    <Route path="/" element={ <Root/> }>
      <Route index element={ <Navigate to={ROUTES.CONTACTS} replace/> }/>
      <Route path={ROUTES.CONTACTS} element={ <ContactsPage /> /* Add props to ContactsPage */ }/>
      <Route path={ROUTES.APPOINTMENTS} element={ <AppointmentsPage /> /* Add props to AppointmentsPage */ }/>
    </Route>
  ));
  
  return (
    <>
    <nav>
      <NavLink to={ROUTES.CONTACTS} activeClassName="active" className="link">
        Contacts
      </NavLink>
      <NavLink to={ROUTES.APPOINTMENTS} activeClassName="active" className="link">
        Appointments
      </NavLink>
    </nav>
    <main>
      <Switch>
        <Route exact path="/">
          <Redirect to={ROUTES.CONTACTS} />
        </Route>
        <Route path={ROUTES.CONTACTS}>
           {/* Add props to ContactsPage */}
          <ContactsPage contacts={contacts} setContact={setContact} delete={deleteItem} />
        </Route>
        <Route path={ROUTES.APPOINTMENTS}>
          {/* Add props to AppointmentsPage */}
          <AppointmentsPage appointments={appointments} setAppointment={setAppointment} contacts={contacts} delete={deleteItem}/>
        </Route>
      </Switch>
    </main>
  </>
  );
}

export default App;
